DROP TABLE IF EXISTS `business_cards`;
CREATE TABLE IF NOT EXISTS `business_cards` (
`id` int(11) NOT NULL AUTO_INCREMENT,
`name` varchar(255) COLLATE utf16_bin NOT NULL,
`profession` varchar(255) COLLATE utf16_bin NOT NULL,
`address` text COLLATE utf16_bin NOT NULL,
`mobile` varchar(16) COLLATE utf16_bin NOT NULL,
`email` varchar(255) COLLATE utf16_bin NOT NULL,
`website` varchar(255) COLLATE utf16_bin NOT NULL,
PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf16 COLLATE=utf16_bin AUTO_INCREMENT=0 ;
